import './App.css';
import MainTable from './main_table';
import data from './data.json';

function getData() {
  return data;
}

function App() {

  const contentRef = useRef([]);//React.createRef();
  contentRef.current = [];

  var json = getData();
  const headersText = ["Region name",
                  "Region alpha code",
                  "Value",
                  "Reference date"];





  keypressHandler = event => {
    if (event.key === "Enter") {
        //this.setState({ value: this.inputRef.current.value });
        //console.log(Array.from(this.headersRef.current.cells));
        Array.from(this.headersRef.current.cells).map((textInput) => {
            //textInput.setState
            textInput.childNodes[0].blur();
        });
        return null;
        //this.inputRef.current.blur();
        //this.inputRef.current.value = "";
    }
  };

  renderHeaders =()=>{
    let headers = [];
    this.state.headersText.map((header,i) => {
        headers.push(<th>
                     <input type="text" 
                        //onChange={(header)=>{this._handleChangeEvent(header);}} 
                        onKeyPress={event => this.keypressHandler(event)} 
                        defaultValue={header}/>
                     <a href="#" onClick={this.selectOrderColumn}>
                         <i class="fa">&#xf014;</i></a> 
                     </th>);
        return headers; 
    }); 
    return headers;
  };


  return (
    <div className="App">
      <table>
        <thead>
          <tr ref={this.headersRef}>
              {this.renderHeaders()}
          </tr>
        </thead>
        <tbody ref={this.contentRef}>
          {this.state.json.map((data, i) => {
          return (
              <tr key={i}>
              <td>{data.region_name}</td>
              <td>{data.region_alpha_code}</td>
              <td>{data.value}</td>
              <td>{data.ref_date}</td>
              </tr>
          )
          })}
        </tbody>
      </table>  
    </div>
  );
}

export default App;
