import './App.css';
import MainTable from './main_table';
import data from './data.json';
import React, {useRef,useEffect,useState} from "react";

function getData() {
  return data;
}

function App() {

  const headersRef = React.createRef();
  const contentRef = useRef([]);//React.createRef();
  contentRef.current = [];


  var json = getData();
  const headersText = ["Region name",
                  "Region alpha code",
                  "Value",
                  "Reference date"];




  const keypressHandler= (event) => {
    if (event.key === "Enter") {
        //this.setState({ value: this.inputRef.current.value });
        //console.log(Array.from(this.headersRef.current.cells));
        Array.from(headersRef.current.cells).map((textInput) => {
            //textInput.setState
            textInput.childNodes[0].blur();
        });
        return null;
        //this.inputRef.current.blur();
        //this.inputRef.current.value = "";
    }
  };


  const selectOrderColumn= () => {
    return console.log(this.contentRef.current.children); 
  };

  const renderHeaders = () => {
    let headers = [];
    headersText.map((header,i) => {
        headers.push(<th>
                     <input type="text" 
                        //onChange={(header)=>{this._handleChangeEvent(header);}} 
                        onKeyPress={event => keypressHandler(event)} 
                        defaultValue={header}/>
                     <a href="#" onClick={selectOrderColumn}>
                         <i class="fa">&#xf014;</i></a> 
                     </th>);
        return headers; 
    }); 
    return headers;
  }


  return (
    <div className="App">
      <table>
        <thead>
          <tr ref={headersRef}>
              {renderHeaders()}
          </tr>
        </thead>
        <tbody ref={contentRef}>
          {json.map((data, i) => {
          return (
              <tr key={i}>
              <td>{data.region_name}</td>
              <td>{data.region_alpha_code}</td>
              <td>{data.value}</td>
              <td>{data.ref_date}</td>
              </tr>
          )
          })}
        </tbody>
      </table>  
    </div>
  );
}

export default App;
