class MainTable extends React.Component {


    constructor(props) {
        super(props)
        this.headersRef = React.createRef();
        this.contentRef = React.createRef(); 
        this.state = {
            json: [],
            headersText: ["Region name",
                          "Region alpha code",
                          "Value",
                          "Reference date"]
        }
    }
    /*
    _handleChangeEvent(val) {
        return val;
    }*/


    keypressHandler = event => {
        if (event.key === "Enter") {
            //this.setState({ value: this.inputRef.current.value });
            //console.log(Array.from(this.headersRef.current.cells));
            Array.from(this.headersRef.current.cells).map((textInput) => {
                //textInput.setState
                textInput.childNodes[0].blur();
            });
            return null;
            //this.inputRef.current.blur();
            //this.inputRef.current.value = "";
        }
    };

    selectOrderColumn= () => {
        console.log(this.contentRef);
    };

    renderHeaders() {
        let headers = [];
        this.state.headersText.map((header) => {
            headers.push(<th>
                         <input type="text" 
                            //onChange={(header)=>{this._handleChangeEvent(header);}} 
                            onKeyPress={event => this.keypressHandler(event)} 
                            defaultValue={header}/>
                         <a href="#" onClick={this.selectOrderColumn()}>
                             <i class="fa">&#xf014;</i></a> 
                         </th>);
            return headers; 
        }); 

        return headers; 

    }

    componentDidMount() {
        this.setState((prevState) => {
            return {
                json: getData()
            }
        })
    }

    render() {
        return (
            <div>
                <table>
                    <thead>
                        <tr ref={this.headersRef}>
                            {this.renderHeaders()}
                        </tr>
                    </thead>
                    <tbody ref={this.contentRef}>
                        {this.state.json.map((data, i) => {
                        return (
                            <tr key={i}>
                            <td>{data.region_name}</td>
                            <td>{data.region_alpha_code}</td>
                            <td>{data.value}</td>
                            <td>{data.ref_date}</td>
                            </tr>
                        )
                        })}
                    </tbody>
                </table>
            </div>
        )
    }
}

export default MainTable;






