import './App.css';
import MainTable from './main_table';
import data from './data.json';
import React, {useRef,useEffect,useState} from "react";

function getData() {
  return data;
}

function App() {

  const headersRef = useRef([]);
  headersRef.current = []; 
  const rowsRef = useRef([]);
  rowsRef.current = [];


  var json = getData();
  const headersText = ["Region name",
                       "Region alpha code",
                       "Value",
                       "Reference date"];


  const renderHeaders = () => {
    let headers = [];
    headersText.map((header,i) => {
        headers.push(<th ref={addHeaderToRefs}>
                     <input type="text" 
                        //onChange={(header)=>{this._handleChangeEvent(header);}} 
                        onKeyPress={event => keypressHandler(event,i)} 
                        defaultValue={header}/>
                     <a href="#" onClick={selectOrderColumn}>
                         <i class="fa">&#xf014;</i></a> 
                     </th>);
        return headers; 
    }); 
    return headers;
  }

  const keypressHandler= (event,currentPos) => {
    if (event.key === "Enter") {


        //this.setState({ value: this.inputRef.current.value });
        //console.log(Array.from(this.headersRef.current.cells));

        headersRef.current[currentPos].childNodes[0].blur();
        /* 
        Array.from(headersRef.current.cells).map((textInput) => {
            //textInput.setState
            textInput.childNodes[0].blur();
        });*/
        return null;
        //this.inputRef.current.blur();
        //this.inputRef.current.value = "";*/
    }
  };

  const selectOrderColumn= () => {
    return console.log(this.rowsRef.current.children); 
  };

  const addHeaderToRefs = (el) =>{
    if(el!=null){
      headersRef.current.push(el);
    }
    console.log(headersRef.current);
  };

  const addRowToRefs = (el) =>{
    if(el!=null){
      rowsRef.current.push(el);
    }
    //console.log(rowsRef.current);
  };

  return (
    <div className="App">
      <table>
        <thead>
          <tr ref={headersRef}>
              {renderHeaders()}
          </tr>
        </thead>
        <tbody>
          { json.map((data, i) => {
            return (
                <tr key={i} ref={addRowToRefs}>
                <td>{data.region_name}</td>
                <td>{data.region_alpha_code}</td>
                <td>{data.value}</td>
                <td>{data.ref_date}</td>
                </tr>
            )
          })}
        </tbody>
      </table>  
    </div>
  );
}

export default App;
