import React from "react";

import data from './data.json';

function getJson() {
    return data;
  }

  class MainTable extends React.Component {
    constructor(props) {
      super(props)
      this.state = {
        json: []
      }
    }

    componentDidMount() {
      this.setState((prevState) => {
        return {
          json: getJson()
        }
      })
    }

    render() {
      return (
        <div>
          <table>
            <thead>
              <th>First Name</th>
              <th>Last Name</th>
            </thead>
            <tbody>
              {this.state.json.map((data, i) => {
                return (
                  <tr key={i}>
                    <td>{data.firstname}</td>
                    <td>{data.lastname}</td>
                  </tr>
                )
              })}
            </tbody>
          </table>
        </div>
      )
    }
  }

























